import React, { useState, useEffect } from 'react';
import Board from './Board';

function GamePage({ gameMode, playerSymbol }) {
  const [currentTurn, setCurrentTurn] = useState('X');
  const [scores, setScores] = useState({ wins: 0, draws: 0 });
  const [gameResult, setGameResult] = useState(null);

  const computerSymbol = playerSymbol === 'X' ? 'O' : 'X'; // 컴퓨터의 기호

  useEffect(() => {
    const storedScores = JSON.parse(localStorage.getItem('ticTacToeScores')) || { wins: 0, draws: 0 };
    setScores(storedScores);
  }, []);

  useEffect(() => {
    localStorage.setItem('ticTacToeScores', JSON.stringify(scores));
  }, [scores]);

  const handleGameEnd = (result) => {
    if (result === 'DRAW') {
      setScores((prev) => ({ ...prev, draws: prev.draws + 1 }));
    } else if (result === playerSymbol) {
      setScores((prev) => ({ ...prev, wins: prev.wins + 1 }));
    }
    setGameResult(result);
  };

  const resetBoard = () => {
    setGameResult(null);
    setCurrentTurn('X');
    // 추가: Board 초기화
    setBoard(Array(9).fill(null));
};

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <h2>{currentTurn} Turn</h2>
        <button onClick={resetBoard}>새로고침</button>
      </div>
      <Board
        currentTurn={currentTurn}
        setCurrentTurn={setCurrentTurn}
        onGameEnd={handleGameEnd}
        gameMode={gameMode}
        playerSymbol={playerSymbol}
        computerSymbol={computerSymbol} // 컴퓨터 기호 전달
      />
      <div>
        <p>승리: {scores.wins}</p>
        <p>무승부: {scores.draws}</p>
      </div>
      {gameResult && (
        <div>
          <h2>{gameResult === 'DRAW' ? '무승부!' : `${gameResult} 승리!`}</h2>
          <button onClick={resetBoard}>NEXT ROUND</button>
          <button onClick={() => window.location.reload()}>QUIT</button>
        </div>
      )}
    </div>
  );
}

export default GamePage;
