import React, { useState, useEffect } from 'react';

function Board({ currentTurn, setCurrentTurn, onGameEnd, gameMode, playerSymbol, computerSymbol }) {
  const [board, setBoard] = useState(Array(9).fill(null)); // 보드 상태 초기화
  const [hoverIndex, setHoverIndex] = useState(null);

  useEffect(() => {
    // 컴퓨터 턴 처리
    if (gameMode === 'COMPUTER' && currentTurn === computerSymbol && !onGameEnd) {
      setTimeout(handleComputerTurn, 500);
    }
  }, [currentTurn]);

  const checkWinner = (board) => {
    const winPatterns = [
      [0, 1, 2], [3, 4, 5], [6, 7, 8],
      [0, 3, 6], [1, 4, 7], [2, 5, 8],
      [0, 4, 8], [2, 4, 6],
    ];
    for (let pattern of winPatterns) {
      const [a, b, c] = pattern;
      if (board[a] && board[a] === board[b] && board[a] === board[c]) {
        return board[a];
      }
    }
    return board.includes(null) ? null : 'DRAW';
  };

  const handleClick = (index) => {
    if (board[index] || onGameEnd) return; // 이미 채워진 칸이거나 게임 종료 시 무시

    const newBoard = [...board];
    newBoard[index] = currentTurn; // 현재 턴의 기호로 칸 채우기
    setBoard(newBoard); // 보드 상태 업데이트

    const winner = checkWinner(newBoard); // 승리 체크
    if (winner) {
      onGameEnd(winner); // 게임 종료 처리
    } else {
      if (gameMode === 'COMPUTER' && currentTurn === playerSymbol) {
        setCurrentTurn(computerSymbol); // 컴퓨터 턴으로 전환
      } else {
        setCurrentTurn((prev) => (prev === 'X' ? 'O' : 'X')); // 다음 턴 전환
      }
    }
  };

  const handleComputerTurn = () => {
    const availableIndices = board
      .map((cell, index) => (cell === null ? index : null))
      .filter((val) => val !== null); // 빈 칸 필터링
    if (availableIndices.length === 0) return; // 빈 칸이 없으면 종료

    const randomIndex = availableIndices[Math.floor(Math.random() * availableIndices.length)];
    const newBoard = [...board];
    newBoard[randomIndex] = computerSymbol; // 컴퓨터 기호로 빈 칸 채우기
    setBoard(newBoard); // 보드 상태 업데이트

    const winner = checkWinner(newBoard);
    if (winner) {
      onGameEnd(winner); // 게임 종료 처리
    } else {
      setCurrentTurn(playerSymbol); // 플레이어 턴으로 전환
    }
  };

  const handleMouseEnter = (index) => {
    if (!board[index]) setHoverIndex(index); // 빈 칸일 때만 hover
  };

  const handleMouseLeave = () => {
    setHoverIndex(null);
  };

  const resetBoard = () => {
    setBoard(Array(9).fill(null)); // 보드 초기화
    setCurrentTurn('X'); // 선공으로 설정
  };

  return (
    <div>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 100px)', gap: '10px' }}>
        {board.map((cell, index) => (
          <div
            key={index}
            onClick={() => handleClick(index)} // 클릭 이벤트 처리
            onMouseEnter={() => handleMouseEnter(index)} // hover 시작
            onMouseLeave={handleMouseLeave} // hover 종료
            style={{
              width: '100px',
              height: '100px',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              fontSize: '24px',
              border: '1px solid black',
              position: 'relative',
              backgroundColor: cell ? '#f0f0f0' : '',
              cursor: cell ? 'not-allowed' : 'pointer',
            }}
          >
            {cell || (hoverIndex === index && !cell && (
              <span
                style={{
                  position: 'absolute',
                  fontSize: '24px',
                  color: 'rgba(0, 0, 0, 0.5)',
                }}
              >
                {currentTurn}
              </span>
            ))}
          </div>
        ))}
      </div>
      <button onClick={resetBoard} style={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px' }}>
        새로고침
      </button>
    </div>
  );
}

export default Board;
