import React, { useState, useEffect } from 'react';

function Board({ currentTurn, setCurrentTurn, onGameEnd, gameMode, playerSymbol, computerSymbol }) {
  const [board, setBoard] = useState(Array(9).fill(null));

  useEffect(() => {
    // 컴퓨터가 선공으로 X를 두지 않는 문제 해결
    if (gameMode === 'COMPUTER' && currentTurn === computerSymbol) {
      handleComputerTurn(board); // 컴퓨터가 선공으로 X를 둔다
    }
  }, [currentTurn, board, gameMode, computerSymbol]);

  const checkWinner = (board) => {
    const winPatterns = [
      [0, 1, 2], [3, 4, 5], [6, 7, 8], // 가로
      [0, 3, 6], [1, 4, 7], [2, 5, 8], // 세로
      [0, 4, 8], [2, 4, 6],            // 대각선
    ];
    for (let pattern of winPatterns) {
      const [a, b, c] = pattern;
      if (board[a] && board[a] === board[b] && board[a] === board[c]) {
        return board[a];  // 승자가 있으면 해당 기호 리턴
      }
    }
    return board.includes(null) ? null : 'DRAW';  // 무승부
  };

  const handleClick = (index) => {
    // 이미 클릭된 칸이거나 게임이 종료된 상태에서는 동작하지 않음
    if (board[index] || (gameMode === 'COMPUTER' && currentTurn === computerSymbol) || onGameEnd) return;
  
    // 클릭한 칸 업데이트
    const newBoard = [...board];
    newBoard[index] = currentTurn;  // 클릭한 칸에 X 또는 O 추가
    setBoard(newBoard);
  
    // 승리 조건 확인
    const winner = checkWinner(newBoard);
    if (winner) {
      onGameEnd(winner);
    } else {
      // 컴퓨터 모드일 경우 컴퓨터 턴으로 전환
      if (gameMode === 'COMPUTER' && currentTurn === playerSymbol) {
        setCurrentTurn(computerSymbol);
      } else {
        // 다음 턴으로 전환
        setCurrentTurn((prev) => (prev === 'X' ? 'O' : 'X'));
      }
    }
  };

  const handleComputerTurn = (board) => {
    // 컴퓨터가 선공으로 X를 두는 로직
    const availableIndices = board.map((cell, index) => (cell === null ? index : null)).filter((val) => val !== null);
    if (availableIndices.length === 0) return;

    // 컴퓨터가 무작위로 선택
    const randomIndex = availableIndices[Math.floor(Math.random() * availableIndices.length)];
    const newBoard = [...board];
    newBoard[randomIndex] = computerSymbol;  // 컴퓨터의 기호를 둔다
    setBoard(newBoard);

    // 승리 조건 확인
    const winner = checkWinner(newBoard);
    if (winner) {
      onGameEnd(winner);
    } else {
      setCurrentTurn(playerSymbol); // 컴퓨터 턴 후 플레이어 턴으로
    }
  };

  return (
    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 100px)', gap: '10px' }}>
      {board.map((cell, index) => (
        <div
          key={index}
          onClick={() => handleClick(index)}
          style={{
            width: '100px',
            height: '100px',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            fontSize: '24px',
            border: '1px solid black',
            position: 'relative',
            backgroundColor: cell ? '#f0f0f0' : '',
            cursor: cell ? 'not-allowed' : 'pointer',
          }}
        >
          {cell} {/* 클릭한 칸에 X 또는 O 기호 표시 */}
        </div>
      ))}
    </div>
  );
}

export default Board;
