import React from 'react';

function StartPage({ startGame }) {
  const handleGameStart = (mode) => {
    // 버튼 클릭 시 게임 시작
    const playerSymbol = 'X'; // 기본 플레이어 심볼 (X)
    startGame(mode, playerSymbol);
  };

  return (
    <div
      style={{
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        backgroundColor: '#1A2A33', // 배경색 설정
      }}
    >
      <h1 style={{ color: '#fff', fontSize: '36px', marginBottom: '40px' }}>
        틱택토 게임
      </h1>
      <div
        style={{
          display: 'flex',
          flexDirection: 'column',
          gap: '20px',
        }}
      >
        {/* 컴퓨터와의 대결 */}
        <button
          onClick={() => handleGameStart('COMPUTER')}
          style={{
            padding: '20px 40px',
            fontSize: '20px',
            color: '#1A2A33',
            backgroundColor: '#31C3BD',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
          }}
        >
          NEW GAME (VS CPU)
        </button>
        {/* 플레이어 간 대결 */}
        <button
          onClick={() => handleGameStart('PLAYER')}
          style={{
            padding: '20px 40px',
            fontSize: '20px',
            color: '#1A2A33',
            backgroundColor: '#F2B137',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
          }}
        >
          NEW GAME (VS PLAYER)
        </button>
      </div>
    </div>
  );
}

export default StartPage;
